import React, { useState, useEffect } from 'react';
import axios from 'axios';


const Adduser = () => {
    
const [ville, setville] = useState([]);
const [admin, setadmin] = useState([]);
const [email, setemail] = useState("");
const [noms, setnoms] = useState("");
const [password, setpassword] = useState("");
const [villes, setvilles] = useState("");
const [statut, setstatut] = useState("");
let token = 'Bearer 1|XXfI3BOr01VoBYgF8nC27oD7KEN1Oh3EULg68Q4a';
    useEffect(() => {
        axios.get(`http://localhost:5000/api/admin`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setadmin(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/ville`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setville(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    return(
        <>
           <div className="card">
                <div className="card-body">
                  <h5 className="card-title"> <a href="" data-bs-toggle="modal" data-bs-target="#addadmin" className="btn btn-primary"> + Utilisateur</a></h5>
                  <div className="table-responsive">
                    <table
                      id="zero_config"
                      className="table table-striped table-bordered"
                    >
                      <thead>
                        <tr>
                          <th>Email</th>
                          <th>Noms</th>
                          <th>Ville</th>
                          <th>Statut</th>
                          <th>Etat</th>
                          <th>Dates</th>
                        </tr>
                      </thead>
                      <tbody>
                      {
                            admin.map((e) => {
                                return (
                                    <tr>
                                        <td>{e.nom}</td>
                                        <td>{e.noms}</td>
                                        <td>{e.ville}</td>
                                        <td>{e.status}</td>
                                        <td>{e.etat}</td>
                                        <td>{e.created_at}</td>
                                        <td>
                                            <button className="fa fa-edit btn btn-danger"></button>
                                            <button className="fa fa-edit btn btn-primary"></button>
                                        </td>
                                    </tr>
                                );
                            })
                        }
                    </tbody>
                    </table>
                  </div>
                </div>
              </div>

              <div className="modal fade" id="addadmin" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div className="modal-dialog">
        <div className="modal-content">
        <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">Utilisateur + </h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
            <div className="modal-body">
                <form action="">
                    <div>
                        <label for="">Noms</label>
                        <input type="text"  className="form-control" />
                    </div>
                    <div>
                        <label for="">Login Utiliateur</label>
                        <input type="tex" className="form-control" />
                    </div>
                    <div>
                        <label for="">Password</label>
                        <input type="password" className="form-control" />
                    </div>
                    <div>
                        <label for="">Site Utilisateur</label>
                        <select name="site" id="" className="form-control">
                            {
                                ville.map((e) => {
                                    return(
                                        <option value="{e.id}">{e.ville}</option>
                                    );
                                })
                            }
                        </select>
                    </div>
                    <div>
                        <label for="">Statut Utilisateur</label>
                        <select name="site" id="" className="form-control">
                            <option value="">Admin</option>
                            <option value="">Utilisateur</option>
                            <option value="">Menager</option>
                            <option value="">Auditeur</option>
                        </select>
                    </div>
                </form>
            </div>
            <div className="modal-footer">
                <button type="submit" className="btn btn-primary">Creer un utilisateur</button>
            </div>
        </div>
    </div>
</div>
        </>
    );
}

export default Adduser;
