import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

const Prelevement = () => {
    //formulaire
    const [numBon, setnumBon] = useState("");
    const [immatriculation, setimmatriculation] = useState("");
    const [Qte, setQte] = useState("");
    const [kilometrage, setkilometrage] = useState("");
    const [chauffeurs, setchauffeurs] = useState("");
    const [carburant, setcarburant] = useState("");
    const [commentaire, setcommentaire] = useState("");
    
    //condition pour le formulaire
    if(numBon == ""  || Qte == "" || kilometrage == "" || carburant == "" || commentaire == ""){
        Swal.fire({
            icon : 'error',
            text : 'Veuillez remplire tous les champs svp !!!',
            confirmButtonText: 'OK'
       })
    }
    //affichage
    const [plein,  setplain] = useState([]);
    const [chauffeur,  setchauffeur] = useState([]);
    let token = 'Bearer 1|XXfI3BOr01VoBYgF8nC27oD7KEN1Oh3EULg68Q4a';
    useEffect(() => {
        axios.get(`http://localhost:5000/api/plein`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setplain(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/chauffeur`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setchauffeur(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    return (
        <>
            <div className="card">
                <div className="card-body">
                  <h5 className="card-title"> <a href="" data-bs-toggle="modal" data-bs-target="#addadmin" className="btn btn-primary"> + Prelevement</a></h5>
                  <div className="table-responsive">
                    <table
                      id="zero_config"
                      className="table table-striped table-bordered"
                    >
                      <thead>
                        <tr>
                          <th>immatriculation</th>
                          <th>Utilisateur</th>
                          <th>Matricule</th>
                          <th>Qte</th>
                          <th>Kilometrage</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                        {
                            plein.map((e) => {
                                return (
                                    <tr>
                                        <td>{e.immatriculation}</td>
                                        <td>{e.user}</td>
                                        <td>{e.matricule_ch}</td>
                                        <td>{e.qteplein}</td>
                                        <td>{e.kilometrage}</td>
                                        <td>
                                            <button className="fa fa-edit btn btn-danger"></button>
                                            <button className="fa fa-edit btn btn-primary"></button>
                                        </td>
                                    </tr>
                                );
                            })
                        }
                    </tbody>
                    </table>
                  </div>
                </div>
              </div>


<div className="modal fade" id="addadmin" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div className="modal-dialog">
        <div className="modal-content">
        <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">Prelevement + </h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
            <div className="modal-body">
                <form action="">
                    <div>
                        <label for="">Numero de bon</label>
                        <input type="text" className="form-control" />
                    </div>
                    <div>
                    <label for="">Immatriculation</label>
                            <select name="" id="" className="form-control">
                                    {
                                        chauffeur.map((e) => {
                                            return(
                                                <option value={e.id}>{e.matricule_ch}</option>
                                            );
                                        })  
                                    }
                            </select>
                    </div>
                    <div>
                        <label for="">Quantité</label>
                        <input type="number" className="form-control" />
                    </div>
                    <div>
                        <label for="">Kilometrage</label>
                        <input type="number" className="form-control" />
                    </div>
                    <div>
                        <label for="">Chauffeur</label>
                        <select name="" id="" className="form-control">
                        {
                                chauffeur.map((e) => {
                                    return(
                                        <option value="{e.id}">{e.nom}</option>
                                    )
                                })
                            }
                        </select>
                    </div>
                    <div>
                        <label for="">Carburant</label>
                        <input type="text" className="form-control" />
                    </div>
                    <div>
                        <label for="">Kilometrage</label>
                       <textarea name="" id="" cols="10" rows="3" className="form-control"></textarea>
                    </div>
                </form>
            </div>
            <div className="modal-footer">
                <button type="submit" className="btn btn-primary">Enregister</button>
            </div>
        </div>
    </div>
</div>
        </>
    );
}
export default Prelevement;
