import React, { useState, useEffect } from "react";
import axios from "axios";

const AffchageTous = () => {
  const [plein,  setplain] = useState([])
    let token = `Bearer ${localStorage.getItem("token")}` ;
    let n = 1
    useEffect(() => {
        axios.get(`http://localhost:5000/api/plein`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setplain(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
   return (
        <>
        <div class="card">
                <div class="card-body">
                  <center>
                  <h5 class="card-title"> Liste de tous le prelevements de carburant</h5>
                  </center>
                  
                  <div class="table-responsive">
                    <table
                      id="zero_config"
                      class="table table-striped table-bordered"
                    >
                      <thead>
                      <tr>
                          <th>N°</th>
                          <th>immatriculation</th>
                          <th>Utilisateur</th>
                          <th>Matricule</th>
                          <th>Qte</th>
                          <th>Kilometrage</th>
                          <th>Numero</th>
                          <th>Detail</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                      {
                            plein.map((e) => {
                                return (
                                    <tr>
                                        <td>{ n++}</td>
                                        <td>{e.immatriculation}</td>
                                        <td>{e.user}</td>
                                        <td>{e.matricule_ch}</td>
                                        <td>{e.qteplein}</td>
                                        <td>{e.kilometrage}</td>
                                        <td>{e.num}</td>
                                        <td>
                                           <button className="btn btn-primary">
                                              <i className="fa fa-edit"></i>
                                           </button>
                                        </td>
                                        <td>
                                            <button className="fa fa-edit btn btn-danger"></button>
                                            <button data-bs-toggle="modal" data-bs-target="#updatePrelevement" className="fa fa-edit btn btn-primary"></button>
                                        </td>
                                    </tr>
                                );
                            })
                        }
                      </tbody>
                    </table>
                  </div>
                </div>
              </div> 
        </>
   );
}

export default AffchageTous;
