import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import Swal from "sweetalert2";

const Prelevement = () => {
    //formulaire
    let token = `Bearer ${localStorage.getItem("token")}` ;
    const [numBon, setnumBon] = useState("");
    const [immatriculation, setimmatriculation] = useState("");
    const [Qte, setQte] = useState("");
    const [kilometrage, setkilometrage] = useState("");
    const [chauffeurs, setchauffeurs] = useState("");
    const [dates, setDates] = useState("");
    const [carburant, setcarburant] = useState("");
    const [commentaire, setcommentaire] = useState("");
    
    //condition pour le formulaire
   const url = 'http://localhost:5000/api/plein';
   const Enregistrer = () => {
        if(numBon == ""  || Qte == "" || kilometrage == "" || carburant == "" || commentaire == ""){
            Swal.fire({
                icon : 'error',
                text : 'Veuillez remplire tous les champs svp !!!',
                confirmButtonText: 'OK'
            })
        }else{
            axios.post(url, {
                numBon : numBon,
                immatriculation : immatriculation,
                Qte : Qte,
            }).then((response) => {
                Swal.fire({
                    icon : 'success',
                    text : 'Success',
                    confirmButtonText: 'OK'
                });
               
            }).catch( (err) => {
                alert('Erreur' + err);
            }); 
        }
    }
    //affichage
    const [plein,  setplain] = useState([]);
    const [chauffeur,  setchauffeur] = useState([]);
    const [vehicule,  setvehicule] = useState([]);
    useEffect(() => {
        axios.get(`http://localhost:5000/api/plein`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setplain(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/chauffeur`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setchauffeur(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/vehicule`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setvehicule(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    return (
        <>
            <div className="card">
                <div className="card-body">
                  <h5 className="card-title"> <a href="" data-bs-toggle="modal" data-bs-target="#addadmin" className="btn btn-primary"> + Prelevement</a></h5>
                  <div className="table-responsive">
                    <table
                      id="zero_config"
                      className="table table-striped table-bordered"
                    >
                      <thead>
                        <tr>
                          <th>immatriculation</th>
                          <th>Utilisateur</th>
                          <th>Matricule</th>
                          <th>Qte</th>
                          <th>Kilometrage</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                        {
                            plein.map((e) => {
                                return (
                                    <tr>
                                        <td>{e.immatriculation}</td>
                                        <td>{e.user}</td>
                                        <td>{e.matricule_ch}</td>
                                        <td>{e.qteplein}</td>
                                        <td>{e.kilometrage}</td>
                                        <td>
                                            <button className="fa fa-edit btn btn-danger"></button>
                                            <button className="fa fa-edit btn btn-primary"></button>
                                        </td>
                                    </tr>
                                );
                            })
                        }
                    </tbody>
                    </table>
                  </div>
                </div>
              </div>


<div className="modal fade" id="addadmin" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div className="modal-dialog">
        <div className="modal-content">
        <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">Prelevement + </h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
            <div className="modal-body">
                <div className="row">
                    <div className="col-md-6">
                        <label for="">Numero de bon</label>
                        <input type="text" onChange={(e) => setnumBon(e.target.value)} className="form-control" />
                    </div>
                    <div  className="col-md-6">
                    <label for="">Immatriculation</label>
                            <select onChange={(e) => setimmatriculation(e.target.value)} id="" className="form-control">
                                    {
                                        vehicule.map((e) => {
                                            return(
                                                <option value={e.id}>{e.immatriculation}</option>
                                            );
                                        })  
                                    }
                            </select>
                    </div>
                    <div  className="col-md-6">
                        <label for="">Date</label>
                        <input type="date" onChange={(e) => setDate(e.target.value)} className="form-control" />
                    </div>
                    <div  className="col-md-6">
                        <label for="">Quantité</label>
                        <input type="number" onChange={(e) => setQte(e.target.value)} className="form-control" />
                    </div>
                    <div className="col-md-6">
                        <label for="">Kilometrage</label>
                        <input type="number" onChange={(e) => setkilometrage(e.target.value)} className="form-control" />
                    </div>
                    <div  className="col-md-6">
                        <label for="">Chauffeur</label>
                        <select name="" id="" onChange={(e) => setchauffeur(e.target.value)} className="form-control">
                        {
                                chauffeur.map((e) => {
                                    return(
                                        <option value="{e.id}">{e.nom}</option>
                                    )
                                })
                            }
                        </select>
                    </div>
                    <div  className="col-md-6" >
                        <label for="">Carburant</label>
                        <input type="text" onChange={(e) => setcarburant(e.target.value)} className="form-control" />
                    </div>
                    <div  className="col-md-6">
                        <label for="">Commentaire</label>
                       <textarea name="" id="" onChange={(e) => setcommentaire(e.target.value)} cols="10" rows="3" className="form-control"></textarea>
                    </div>
                </div>
            </div>
            <div className="modal-footer">
                <button type="submit" onClick={Enregistrer} className="btn btn-primary">Enregister</button>
            </div>
        </div>
    </div>
</div>
        </>
    );
}
export default Prelevement;
