import React, {useEffect, useState} from "react";
import axios from "axios";
import Swal from "sweetalert2";


const Zone = () => {
    const [Zone, setZone] = useState([]);
    let token = `Bearer ${localStorage.getItem("token")}` ;
    const ajouter = () => {
        if(Zone == ""){
            Swal.fire({
                icon : 'error',
                text : 'Veuillez remplire ce champs svp !!!',
                confirmButtonText: 'OK'
            })
        }
    }
    useEffect(() => {
        axios.get(`http://localhost:5000/api/zone`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setZone(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    return(
        <>
            <div className="card">
                <div className="card-body">
                  <h5 className="card-title"> <a href="" data-bs-toggle="modal" data-bs-target="#addZone" className="btn btn-primary"> + Zone</a></h5>
                  <div className="table-responsive">
                    <table
                      id="zero_config"
                      className="table table-striped table-bordered"
                    >
                      <thead>
                        <tr>
                          <th>Zone</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                            {
                                Zone.map((e) => {
                                    return(
                                        <tr>
                                            <td>{e.NomZone}</td>
                                            <td>
                                                <button className="btn btn-primary"><i className="fa fa-edit"></i></button> &nbsp;
                                                <button className="btn btn-primary"><i className="fa fa-edit"></i></button>
                                            </td>
                                        </tr>
                                    )
                                })
                            }
                    </tbody>
                    </table>
                  </div>
                </div>
              </div>

              <div className="modal fade" id="addZone" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
                <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title" id="exampleModalLabel">Zone + </h5>
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                    <div className="modal-body">
                            <div> 
                               <div>
                                <label for="">Zone</label>
                                <input type="text" onChange={(e) => setZone(e.target.value)}   className="form-control" />
                            </div>
                            </div>
                    </div>
                    <div className="modal-footer">
                        <button onClick={ajouter} className="btn btn-primary">Ajouter</button>
                    </div>
                </div>
            </div>
            </div>
        </>
    )
}

export default Zone;
