import React, {useEffect, useState} from "react";
import axios from "axios";
import Swal from 'sweetalert2'; 

const Conge = () => {
    const [chauffeur, setchauffeur] = useState([]);
    const [type_conge, settype_conge] = useState([]);
    let token = `Bearer ${localStorage.getItem("token")}` ;
    const url = 'http://localhost:5000/api/chauffeur';
    useEffect(() => {
        axios.get(`http://localhost:5000/api/chauffeur`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setchauffeur(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/type_conges`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            settype_conge(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    return (
        <>
            <div className="card">
            <div className="card-body">
                <h2><center>Enregistrer conge</center></h2>
                <div className="row">
                        <div className="col-md-6">
                            <label for="">Chauffeur</label>
                            <select name="site"  className="form-control">
                                {
                                    chauffeur.map((e) => {
                                        return(
                                            <>
                                                <option value={e.nom}>{ e.nom + " " + e.prenom}</option>
                                            </>
                                        )
                                    })
                                }
                            </select>
                        </div>
                        <div className="col-md-6">
                            <label for="">Debut congé</label>
                            <input type="date" className="form-control" />
                        </div>
                        <div className="col-md-6">
                            <label for="">Fin congé</label>
                            <input type="date" className="form-control" />
                        </div>
                        <div className="col-md-6">
                            <label for="">Nbr jours ouvrables</label>
                            <input type="number" className="form-control" />
                        </div>
                        <div className="col-md-6">
                            <label for="">Anne de congé</label>
                            <input type="number" className="form-control" /> 
                        </div>
                        <div className="col-md-6">
                            <label for="">Type de congé</label>
                            <select name="site"  className="form-control">
                                {
                                    type_conge.map((e) => {
                                        return(
                                            <>
                                                <option key={e.id} value={e.id}>{e.lib_typ_cong }</option>
                                            </>
                                        )
                                    })
                                }
                            </select>    
                        </div>
                        <div className="col-md-12">
                            <label for="">Commentaire</label>
                            <textarea name="commentaire" id="" className="form-control" cols="30" rows="3"></textarea> <br />  
                        </div>
                        <button className="btn btn-primary col-md-2">Valider</button>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Conge;
