import React, { useState, useEffect } from "react";
import axios from "axios";
import Swal from "sweetalert2";

const Intervention = () => {
    let token = `Bearer ${localStorage.getItem("token")}`;
    const [intervention, setintervention] = useState([]);
    const [vehicule, setvehicule] = useState([]);
    const [reparation, setreparation] = useState([]);
    const [loading, setLoading] = useState(true);
    const [immatriculations, setimmatriculations] = useState("");
    const [debut, setdebut] = useState("");
    const [kilo, setkilo] = useState("");
    const [fournisseur, setfournisseur] = useState("");
    const [typerep, settyperep] = useState("");
    const [desc, setdesc] = useState("");
    const [cout, setcout] = useState("");
    const [multiple, setmultiple] = ([])
    let n = 1;
    const url = 'http://localhost:5000/api/intervention';
    const Enregistrer = () => {
        if (kilo == "" || desc == "" || cout == "") {
            Swal.fire({
                icon: 'error',
                text: 'Veuillez remplire tous les champs svp !!!',
                confirmButtonText: 'OK'
            })
        } else {
            axios.post(url, {
                type_rep: typerep,
                date_debut: debut,
                intervention: fournisseur,
                km1: kilo,
                km2: immatriculations,
                mat_mec: immatriculations,
                cout: cout,
                explicatif: desc,
            }, {
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    Authorization: token
                }
            }
            ).then((response) => {
                Swal.fire({
                    icon: 'success',
                    text: 'Success',
                    confirmButtonText: 'OK'
                });

            }).catch((err) => {
                alert('Erreur' + err);
            });
        }
    }
    useEffect(() => {
        axios.get(`http://localhost:5000/api/type_reparation`,
            {
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    Authorization: token
                }
            }
        ).then((response) => {
            setreparation(response.data.data);
            setLoading(false);
        }).catch((error) => {
            alert(error)
        })
    }, [])

    useEffect(() => {
        const sites = JSON.parse(localStorage.getItem("site"))
        if (sites != "") {
            sites.map((sit) => {
                axios.get(`http://localhost:5000/api/intervention/${sit.idSite}`,
                    {
                        headers: {
                            Accept: 'application/json',
                            'Content-Type': 'application/json',
                            Authorization: token
                        }
                    }
                ).then((response) => {
                    setintervention(response.data.data);
                    setLoading(false);
                }).catch((error) => {
                    alert(error + "intervention")
                })
            })
        }
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/vehicule`,
            {
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
