import React ,{ useState, useEffect } from "react";
import axios from "axios";
import Swal from "sweetalert2";

const Recherche = () => {
const [debut, setdebut] = useState("");
const [fin, setfin] = useState("");
const [datax, setdata] = useState([]);
const [loading, setLoading] = useState(false);
const url = `http://localhost:5000/api/recherche_consomation/${debut}/${fin}`;	
let token = `Bearer ${localStorage.getItem("token")}` ;

const RechercheBtn = () => {
        setLoading(true)
        
        axios.get(url,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            if(response.status === 200){
                setdata(response.data.data);
                setLoading(false)
            }else if(response.status === 404){
                Swal.fire({
                    icon: 'error',
                    title: 'error',
                    text: 'Veuillez choisir la date debut et la date fin !!!',
                })
            }
            
        }).catch((error) => {
            setLoading(false)
        })
}
if(loading){
    return <h4><center><br /><i className="fa fa-pulse fa-spinner" style={{fontSize : 50}}></i></center></h4>
}
    return(
        <div class="card">
           <br />
                <div class="row">
                    <div class="col-md-3">
                        <input type="date" onChange={(e) => setdebut(e.target.value)} class="form-control" placeholder="Date debut" /> 
                    </div>
                    <div class="col-md-3">
                        <input type="date" onChange={(e) => setfin(e.target.value)} class="form-control" placeholder="Date fin" /> 
                    </div>
                    <div class="col-md-3">
                        <button onClick={RechercheBtn} class="btn btn-primary"> <i class="fas fa-search"></i></button>
                    </div>
                </div>
                <div className="card-body">
                  <div className="table-responsive">
                    <table
                      id="zero_config"
                      className="table table-striped table-bordered"
                    >
                      <thead>
                        <tr>
                          <th>immatriculation</th>
                          <th>Utilisateur</th>
                          <th>Matricule</th>
                          <th>Qte</th>
                          <th>Kilometrage</th>
                        </tr>
                      </thead>
                      <tbody>
                        {
                            datax.map((e) => {
                                return (
                                    <tr>
                                        <td>{e.immatriculation}</td>
                                        <td>{e.user}</td>
                                        <td>{e.matricule_ch}</td>
                                        <td>{e.qteplein}</td>
                                        <td>{e.kilometrage}</td>
                                    </tr>
                                );
                            })
                        }
                    </tbody>
                    </table>
                  </div>
                </div>
        </div>
    );
}
export default Recherche;
