import React, {useEffect, useState} from "react";
import axios from "axios";
import Swal from 'sweetalert2';
const Site = () => {
    const [ville, setville] = useState([]);
    const [zone, setzone] = useState([]);
    const [site, setsite] = useState([]);
    let token = 'Bearer 1|XXfI3BOr01VoBYgF8nC27oD7KEN1Oh3EULg68Q4a';
    useEffect(() => {
        axios.get(`http://localhost:5000/api/ville`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setville(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/zone`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setzone(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/site`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setsite(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    const DeleteSite = (id) => {
        axios.delete(`http://localhost:5000/api/site/${id}`,{
            headers : {
                Accept: 'application/json',
                'Content-Type' : 'application/json',
                Authorization : token
            }
        }).then((response) => {
            Swal.fire({
                icon : 'success',
                text : 'Success',
                confirmButtonText: 'OK'
            });
        }).catch((error) => {
            alert(error)
        })
    }
    return (
        <>
           
            <div className="card">
                <div className="card-body">
                  <h5 className="card-title"> <a href="" data-bs-toggle="modal" data-bs-target="#addadmin" className="btn btn-primary"> + Site</a></h5>
                  <div className="table-responsive">
                    <table
                      id="zero_config"
                      className="table table-striped table-bordered"
                    >
                      <thead>
                        <tr>
                          <th>Code</th>
                          <th>Nom site</th>
                          <th>Ville</th>
                          <th>Province</th>
                          <th>Zone</th>
                          <th>Adresse</th>
                          <th>Telephone</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                         {
                            site.map((e) => {
                                return(
                                    <tr>
                                        <td>{e.CodeSite}</td>
                                        <td>{e.nom_site}</td>
                                        <td>{e.IdVille}</td>
                                        <td>{e.nom_site}</td>
                                        <td>{e.IdZone}</td>
                                        <td>{e.adress}</td>
                                        <td>{e.tel}</td>
                                        <td>
                                            <button onClick={() => DeleteSite(e.id)} className="btn btn-danger"><i className="fa fa-edit"></i></button> &nbsp;
                                            <button className="btn btn-primary"><i className="fa fa-edit"></i></button>
                                        </td>
                                    </tr>
                                )
                            })
                         }
                    </tbody>
                    </table>
                    <center>
                    {
                        loading === true && (
                            <p><i className="fa fa-pulse fa-spinner text-primary" style={{fontSize : 40}} ></i></p>
                        )
                    }
                </center>
                  </div>
                </div>
              </div>
             

<div className="modal fade" id="addadmin" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div className="modal-dialog">
        <div className="modal-content">
        <div className="modal-header">
            <h5 className="modal-title" id="exampleModalLabel">Site + </h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
            <div className="modal-body">
                    <div>
                        <label for="">Code site</label>
                        <input type="text"   className="form-control" />
                        <label for="">Nom site</label>
                        <input type="text"   className="form-control" />
                        <label for="">Ville</label>
                        <select name="site"  className="form-control">
                            {
                                ville.map((e) => {
                                    return(
                                        <option value="{e.id}">{e.ville}</option>
                                    );
                                })
                            }
                        </select>
                        <label for="">Zone</label>
                        <select name="site"  className="form-control">
                            {
                                ville.map((e) => {
                                    return(
                                        <option value="{e.id}">{e.ville}</option>
                                    );
                                })
                            }
                        </select>
                        <div>
                        <label for="">Adresse</label>
                        <input type="text"   className="form-control" />
                        <label for="">Telephone</label>
                        <input type="text"   className="form-control" />
                    </div>
                    </div>
            </div>
            <div className="modal-footer">
                <button className="btn btn-primary">Ajouter</button>
            </div>
        </div>
    </div>
</div>
        </>
    )
}
export default Site;
