import React, { useState, useEffect } from "react"
import axios from "axios";
const Affecter = () => {
    let token = 'Bearer 1|XXfI3BOr01VoBYgF8nC27oD7KEN1Oh3EULg68Q4a';
    const [chauffeur, setchauffeur] = useState([]);
    const [affectation, setaffectation] = useState([]);
    useEffect(() => {
        axios.get(`http://localhost:5000/api/chauffeur`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setchauffeur(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
    useEffect(() => {
        axios.get(`http://localhost:5000/api/affectation`,
            {
                headers : {
                    Accept: 'application/json',
                    'Content-Type' : 'application/json',
                    Authorization : token
                }
            }
        ).then((response) => {
            setaffectation(response.data.data);
        }).catch((error) => {
            alert(error)
        })
    }, [])
  return(
    <>
       <div className="card">
            <div className="card-body">
                <div className="row">
                    <div className="col-md-6">
                        <div>
                            <label for="">Site d'affecation</label>
                            <select name=" site"  className="form-control">
                                <option value="admin">Admin</option>
                                <option value="utilisateur">Utilisateur</option>
                                <option value="manager">Menager</option>
                                <option value="auditeur">Auditeur</option>
                            </select>
                        </div>
                        <div>
                            <label for="">Chauffeur</label>
                            <select name="site"  className="form-control">
                                {
                                    chauffeur.map((e) => {
                                        return(
                                            <>
                                                <option value={e.nom}>{ e.nom + " " + e.prenom}</option>
                                            </>
                                        )
                                    })
                                }
                            </select>
                        </div>
                        <div>
                            <label for="">Date d'affectation</label>
                            <input type="date" className="form-control" />
                        </div>
                        <br />
                        <button className="btn btn-primary">Vlider</button>
                    </div>
                </div>
            </div>
        </div>
    </>
  )
}

export default Affecter;
